require_once 'vendor/autoload.php';\n\nuse PhpMimeMailParser\Parser;\n\nfunction clean($text) {\n    $parser = new Parser();\n    $msg = $parser->parse($text);\n    $html = '';\n\n    foreach ($msg->getParts() as $part) {\n        if ($part->getContentType() == 'text/plain') {\n            $html .= '<p>' . $part->getBody() . '</p>';\n        } elseif ($part->getContentType() == 'text/html') {\n            $html .= $part->getBody();\n        }\n    }\n\n    return $html;\n}\n\n$text = '...your MIME data...';\necho clean($text);