// Define custom user roles\nfunction custom_user_roles() {\n    // Admin role\n    $admin_role = add_role('admin', 'Admin');\n    $admin_role->add_cap('manage_sites');\n    $admin_role->add_cap('manage_users');\n\n    // Member Admin role\n    $member_admin_role = add_role('member_admin', 'Member Admin');\n    $member_admin_role->add_cap('manage_content');\n    $member_admin_role->add_cap('add_users');\n\n    // Member User role\n    $member_user_role = add_role('member_user', 'Member User');\n    $member_user_role->add_cap('edit_content');\n}\nadd_action('init', 'custom_user_roles');