<?php\n$login = $_POST['login'];\n$password = $_POST['password'];\n\n// Connect to database using prepared statements\n$dsn = 'mysql:host=localhost;dbname=aun_vox';\n$username = 'root';\n$password_db = 'abc123';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password_db);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    die('Connection failed: ' . $e->getMessage());\n}\n\n// Retrieve stored hashed password\n$stmt = $pdo->prepare('SELECT password FROM customer WHERE login = :login');\n$stmt->bindParam(':login', $login);\n$stmt->execute();\n$stored_hash = $stmt->fetchColumn();\n\n// Compare hashed input password with stored hashed password\nif (password_verify($password, $stored_hash)) {\n    echo "You did it.";\n} else {\n    echo "Wrong.";\n}