require_once 'xmpphp/xmpp.php';\n\n// Set up your XMPP connection\n$conn = new XMPP('your_xmpp_server', 5222, 'your_username', 'your_password', 'your_resource');\n\n// Send a message\n$conn->message('your_contact_jid', 'Hello, world!');\n\n// Receive messages\n$conn->processUntil(array('message', 'presence', 'end_stream'));\n$msg = $conn->processTime(5);\nif ($msg !== null) {\n    echo "Received message: " . $msg->getBody();\n}