// Get the current date\n$currentDate = date('Y-m-d');\n\n// Calculate the next day's date based on the page number\nif (isset($_GET['page'])) {\n    $pageNumber = (int) $_GET['page'];\n    $nextDate = date('Y-m-d', strtotime($currentDate . ' + ' . $pageNumber . ' days'));\n} else {\n    $nextDate = $currentDate;\n}\n\n// Your SQL query with the calculated date\n$query = "SELECT * FROM your_table WHERE date_column = '$nextDate'";