function categorizeMimeType($mimeType) {\n    $categories = [\n        'office' => [\n            'application/msword',\n            'application/vnd.oasis.opendocument.text',\n            'application/pdf',\n            // add more office-related mime types here\n        ],\n        'image' => [\n            'image/jpeg',\n            'image/png',\n            'image/gif',\n            // add more image-related mime types here\n        ],\n        'application' => [\n            'application/json',\n            'application/xml',\n            // add more application-related mime types here\n        ],\n        'compressed' => [\n            'application/gzip',\n            'application/zip',\n            // add more compressed-related mime types here\n        ],\n    ];\n\n    foreach ($categories as $category => $types) {\n        if (in_array($mimeType, $types)) {\n            return $category;\n        }\n    }\n\n    return 'unknown';\n}\n\n// Example usage:\necho categorizeMimeType('application/msword'); // outputs "office"\necho categorizeMimeType('image/jpeg'); // outputs "image"