$to = 'recipient@example.com';\n$subject = 'Email with attached image';\n$message = '<html><body><img src="cid:logo"></body></html>';\n$attachment = 'path/to/logo.png';\n\n$headers = array(\n    'Content-Type: multipart/mixed; boundary="boundary"',\n    'MIME-Version: 1.0'\n);\n\n$message .= "\r\n\r\n--boundary\r\n";\n$message .= "Content-Type: image/png; name=\"logo.png\"\r\n";\n$message .= "Content-Transfer-Encoding: base64\r\n";\n$message .= "Content-ID: <logo>\r\n";\n$message .= base64_encode(file_get_contents($attachment)) . "\r\n";\n$message .= "--boundary--\r\n";\n\nmail($to, $subject, $message, implode("\r\n", $headers));