class Model_Artifact extends LP_Model_Factory_Abstract\n{\n    private $finder;\n\n    public function __construct(ArtifactFinder $finder)\n    {\n        $this->finder = $finder;\n    }\n\n    public function create($params)\n    {\n        return $this->finder->create($params);\n    }\n\n    public function get($params)\n    {\n        return $this->finder->get($params);\n    }\n\n    public function getCollection($params = null)\n    {\n        return $this->finder->getCollection($params);\n    }\n\n    public function factory($row)\n    {\n        return $this->finder->factory($row);\n    }\n}