foreach ($words as $word) {\n  $existingWord = $db->query("SELECT * FROM search_index WHERE word = '$word'");\n  if ($existingWord) {\n    $postIds = $existingWord['post_ids'] . ',' . $postId;\n    $db->query("UPDATE search_index SET post_ids = '$postIds' WHERE word = '$word'");\n  } else {\n    $db->query("INSERT INTO search_index (word, post_ids) VALUES ('$word', '$postId')");\n  }\n}