// Set the session expiration time to 30 minutes\nini_set('session.gc_maxlifetime', 1800); // 1800 seconds = 30 minutes\n\n// Start the session\nsession_start();\n\n// Set the session expiration time for this session\n$_SESSION['expires'] = time() + 1800; // 1800 seconds = 30 minutes\n\n// Check if the session has expired\nif (isset($_SESSION['expires']) && $_SESSION['expires'] < time()) {\n    // Session has expired, destroy it\n    session_destroy();\n    session_unset();\n}