<?php\n// Use PDO or MySQLi prepared statements\n$dsn = 'mysql:host=localhost;dbname=your_database';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n  $pdo = new PDO($dsn, $username, $password);\n  $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n  echo 'Connection failed: ' . $e->getMessage();\n  exit();\n}\n\n$id = $_GET['b'];\n$show = $_GET['p'];\n\n$stmt = $pdo->prepare('UPDATE listing SET show_online = :show WHERE id = :id LIMIT 1');\n$stmt->bindParam(':id', $id);\n$stmt->bindParam(':show', $show);\n$stmt->execute();\n\nif ($stmt->rowCount() > 0) {\n  if ($show == 0) {\n    $return = '<a class="onlineStatus" href="#" onclick="doAjaxPostOnline(1, ' . $id . '); return false;">Post Online</a>';\n  } elseif ($show == 1) {\n    $return = '<a class="onlineStatus" href="#" onclick="doAjaxPostOnline(0, ' . $id . '); return false;">Post Online</a>';\n  }\n  echo json_encode(array('id' => $id, 'return' => $return));\n}