function normalizeTime($input) {\n    // Remove any non-numeric, non-colon, non-dot, non-space characters\n    $input = preg_replace('/[^\d:. ]/', '', $input);\n\n    // Split the input into parts\n    $parts = explode(' ', $input);\n\n    // Check if the input is in "X hours and Y minutes" format\n    if (count($parts) > 1 && strpos($parts[0], 'hour') !== false) {\n        $hours = (int) preg_replace('/\D/', '', $parts[0]);\n        $minutes = (int) preg_replace('/\D/', '', $parts[2]);\n        return sprintf('%02d:%02d', $hours, $minutes);\n    }\n\n    // Check if the input is in "X:Y" format\n    if (strpos($input, ':') !== false) {\n        list($hours, $minutes) = explode(':', $input);\n        return sprintf('%02d:%02d', (int) $hours, (int) $minutes);\n    }\n\n    // Assume the input is a decimal number of minutes\n    $minutes = (int) ($input * 60);\n    $hours = floor($minutes / 60);\n    $minutes %= 60;\n    return sprintf('%02d:%02d', $hours, $minutes);\n}\n\n// Test cases\necho normalizeTime('an hour and a half') . "\n"; // Output: 01:30\necho normalizeTime('27.52') . "\n"; // Output: 00:28\necho normalizeTime('5:24') . "\n"; // Output: 05:24