class _sample extends framework {\n    function contact() {\n        $validator = new Validator();\n\n        $validator->addRule('name', 'Is::Set', 'Please fill in your name.');\n        $validator->addRule('email', 'Is::Email', 'Please fill in your email address.');\n        $validator->addRule('contact', function($value) {\n            return Is::Phone($value) || Is::Mobile($value);\n        }, 'Please fill in your phone (or cell phone) number.');\n        $validator->addRule('message', 'Is::Set', 'Please type a message');\n\n        if ($this->Is->Post() === true) {\n            if ($validator->validate($_POST)) {\n                // do stuff and redirect to "success" / "thank you" page\n            } else {\n                $errors = $validator->getErrors();\n                $this->View('contact_form', $errors);\n            }\n        } else {\n            $this->View('contact_form');\n        }\n    }\n}