$string = 'Hello, World!';\n$key = 'your_secret_key_here'; // use a strong, random key\n$cipher = 'AES-128-CBC'; // a widely used and considered secure cipher\n$iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length($cipher)); // generate a random IV\n\n$encrypted = openssl_encrypt($string, $cipher, $key, 0, $iv);\n\n// store the IV and encrypted data together, e.g.\n$stored_data = base64_encode($iv . $encrypted);\n\n// to decrypt\n$decrypted = openssl_decrypt($encrypted, $cipher, $key, 0, $iv);