$ch = curl_init();\n\n// Set URL and options for the first request\ncurl_setopt($ch, CURLOPT_URL, 'http://example.com/request1');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute the first request\n$response1 = curl_exec($ch);\n\n// Set URL and options for the second request\ncurl_setopt($ch, CURLOPT_URL, 'http://example.com/request2');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute the second request\n$response2 = curl_exec($ch);\n\n// Close the curl connection\ncurl_close($ch);