$url = 'https://example.com/remote-file.mp4'; // replace with the remote file URL\n\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_BINARYTRANSFER, true);\ncurl_setopt($ch, CURLOPT_TIMEOUT, 300); // 5 minutes\n\n$response = curl_exec($ch);\ncurl_close($ch);\n\nif ($response !== false) {\n    $localFile = 'downloaded-file.mp4'; // replace with the desired local file name\n    file_put_contents($localFile, $response);\n    echo "File downloaded and saved as $localFile";\n} else {\n    echo "Failed to download file";\n}