<?php\nrequire_once 'vendor/autoload.php';\n\nuse ApaiIO\Configuration\GenericConfiguration;\nuse ApaiIO\Operations\SubmitFeedOperation;\nuse ApaiIO\ApiFactory;\n\n$accessKey = 'YOUR_ACCESS_KEY';\n$secretKey = 'YOUR_SECRET_KEY';\n$marketplaceId = 'YOUR_MARKETPLACE_ID';\n$merchantId = 'YOUR_MERCHANT_ID';\n\n$config = new GenericConfiguration($accessKey, $secretKey, $marketplaceId, $merchantId);\n$client = new \GuzzleHttp\Client();\n$apiFactory = new ApiFactory($client, $config);\n\n$feed = new \ApaiIO\Data\Feed();\n$feed->setFeedType('_POST_PRODUCT_DATA_');\n$feed->setFeedContent([\n    'Product' => [\n        'ProductType' => 'Books',\n        'Title' => 'Example Book',\n        'Description' => 'This is an example book.',\n        'Price' => '19.99',\n        'Quantity' => '10',\n    ],\n]);\n\n$submitFeedOperation = new SubmitFeedOperation($feed);\n$response = $apiFactory->run($submitFeedOperation);\n\nif ($response->isSuccess()) {\n    echo "Product data sent successfully!";\n} else {\n    echo "Error sending product data: " . $response->getErrorMessage();\n}