class DB {\n    private $table;\n\n    public function __call($method, $args) {\n        $this->table = $method;\n        return $this;\n    }\n\n    public function limit($limit) {\n        echo "You limited $this->table to $limit\n";\n        return $this;\n    }\n\n    public function id($condition) {\n        echo "You filtered $this->table by id $condition\n";\n        return $this;\n    }\n}\n\n$DB = new DB();\n$DB->users()->limit(5); // output: You limited users to 5\n$DB->comments()->limit(3); // output: You limited comments to 3\n\n$DB->comments()->id(" > 3")->limit(10);\n// output: You filtered comments by id  > 3\n// output: You limited comments to 10