class DB {\n    private $table;\n    private $query;\n\n    public function __call($method, $args) {\n        $this->table = $method;\n        $this->query = "SELECT * FROM $method";\n        return $this;\n    }\n\n    public function limit($limit) {\n        $this->query .= " LIMIT $limit";\n        return $this;\n    }\n\n    public function id($condition) {\n        $this->query .= " WHERE id $condition";\n        return $this;\n    }\n\n    public function getQuery() {\n        return $this->query;\n    }\n}\n\n$DB = new DB();\necho $DB->comments()->id(" > 3")->limit(10)->getQuery();\n// output: SELECT * FROM comments WHERE id  > 3 LIMIT 10