<?php\nrequire_once 'facebook.php';\n\n$appapikey = 'xxxxxxx';\n$appsecret = 'xxxxxxx';\n$facebook = new Facebook($appapikey, $appsecret);\n\n// Get a valid session token with publish_stream permission\n$facebook->require_login('publish_stream');\n\n$message = "Will this status show up and allow me to dominate the world?!";\n$uid = $facebook->get_loggedin_user();\n\necho $uid;\n\n// Make the API call with the session token\n$facebook->api_client->stream_publish($message, $uid);