$data = array('key' => 'value'); // data to be sent\n\n$ch = curl_init('http://backend-server.com/process.php'); // initialize curl\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true); // return response as string\ncurl_setopt($ch, CURLOPT_POST, true); // set POST method\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data)); // set POST data\n\n$response = curl_exec($ch); // execute curl\ncurl_close($ch); // close curl\n\necho $response; // output response from backend server