// URL or path to the remote file\n$fileUrl = 'https://example.com/products.txt';\n\n// Initialize an empty array to hold the product data\n$products = [];\n\n// Read the file line by line\n$handle = fopen($fileUrl, 'r');\nif ($handle) {\n    while (($line = fgets($handle)) !== false) {\n        // Trim the line to remove any leading or trailing whitespace\n        $line = trim($line);\n        \n        // Explode the line into an array using the tab character as the delimiter\n        $productData = explode("\t", $line);\n        \n        // Check if we have exactly three pieces of data (number, name, price)\n        if (count($productData) === 3) {\n            // Extract and trim each piece of data, removing any leading or trailing whitespace\n            $number = trim($productData[0]);\n            $name = trim($productData[1]);\n            $price = trim($productData[2]);\n            \n            // Add the product data to the $products array\n            $products[] = [\n                'number' => $number,\n                'name' => $name,\n                'price' => $price,\n            ];\n        } else {\n            // Optional: Handle the case where a line does not contain exactly three pieces of data\n            echo "Skipping invalid line: $line\n";\n        }\n    }\n    fclose($handle);\n} else {\n    // Handle the case where the file could not be opened\n    echo "Could not open file: $fileUrl\n";\n}\n\n// Example: Print the products array\nprint_r($products);