$share_path = '\\\\serverB\\sharedfolder\\file.dummy';\n$username = 'your_username';\n$password = 'your_password';\n\n$handle = fopen($share_path, 'w', false, stream_context_create(array(\n    'http' => array(\n        'method' => 'PUT',\n        'header' => "Authorization: Basic " . base64_encode("$username:$password")\n    )\n)));\n\nif ($handle) {\n    // Write to the file\n    fwrite($handle, 'Hello, World!');\n    fclose($handle);\n} else {\n    print "Failed to open file";\n}