<?php\n$mailSource = 'your_email_source_as_text';\n\n// Parse the email source\n$mail = mailparse_msg_create();\nmailparse_msg_parse($mail, $mailSource);\n\n// Render the email as HTML\n$html = '';\nforeach (mailparse_msg_get_structure($mail) as $part) {\n    $partData = mailparse_msg_get_part($mail, $part);\n    if ($partData['content-type'] == 'text/html') {\n        $html .= $partData['body'];\n    } elseif ($partData['content-type'] == 'multipart/alternative') {\n        // Handle nested multipart emails\n        $subParts = mailparse_msg_get_structure($part);\n        foreach ($subParts as $subPart) {\n            $subPartData = mailparse_msg_get_part($part, $subPart);\n            if ($subPartData['content-type'] == 'text/html') {\n                $html .= $subPartData['body'];\n            }\n        }\n    }\n}\n\n// Display the rendered HTML\necho $html;\n\n// Clean up\nmailparse_msg_free($mail);