require 'vendor/autoload.php';\n\nuse PhpOffice\PhpSpreadsheet\Spreadsheet;\nuse PhpOffice\PhpSpreadsheet\Writer\Xlsx;\n\n$spreadsheet = new Spreadsheet();\n\n// Create three sheets\n$sheet1 = $spreadsheet->getActiveSheet();\n$sheet1->setTitle('Sheet 1');\n$sheet2 = $spreadsheet->createSheet();\n$sheet2->setTitle('Sheet 2');\n$sheet3 = $spreadsheet->createSheet();\n$sheet3->setTitle('Sheet 3');\n\n// Add some data to the sheets\n$sheet1->setCellValue('A1', 'Hello, world!');\n$sheet2->setCellValue('A1', 'This is sheet 2');\n$sheet3->setCellValue('A1', 'This is sheet 3');\n\n// Write the spreadsheet to a file\n$writer = new Xlsx($spreadsheet);\n$writer->save('multiple_sheets.xlsx');\n\n// Download the file\nheader('Content-Type: application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');\nheader('Content-Disposition: attachment; filename="multiple_sheets.xlsx"');\nheader('Cache-Control: max-age=0');\n\nreadfile('multiple_sheets.xlsx');