function calculateWeightedMean($ratings) {\n    $sum = 0;\n    $totalWeight = 0;\n    foreach ($ratings as $rating => $weight) {\n        $sum += $rating * $weight;\n        $totalWeight += $weight;\n    }\n    return $sum / $totalWeight;\n}\n\n$ratings = array(\n    1 => 1,\n    2 => 3,\n    3 => 6,\n    4 => 3,\n    5 => 3\n);\n\n$weightedMean = calculateWeightedMean($ratings);\necho "Weighted Mean: " . $weightedMean;