$pdo = new PDO('mysql:host=localhost;dbname=your_database', 'your_username', 'your_password');\n\n$stmt = $pdo->prepare("\n  SELECT s.*, UNIX_TIMESTAMP(s.`date`) AS `date`, f.FAVORITE,\n         (SELECT COUNT(*) FROM favorites WHERE FAVORITE = s.ID) AS favorite_count\n  FROM songs s\n  INNER JOIN favorites f ON s.ID = f.FAVORITE AND f.USERID = :cookie_hash\n  WHERE s.date >= DATE_SUB(NOW(), INTERVAL 2 WEEK)\n  ORDER BY s.date DESC\n");\n\n$stmt->bindParam(':cookie_hash', $cookie_hash);\n\n$stmt->execute();\n\n$result = $stmt->fetchAll(PDO::FETCH_ASSOC);