$url = 'https://example.com'; // Suspicious site's URL\n$hash = hash('sha256', $url);\n\n// Load the malware and phish lists\n$malwareList = file_get_contents('https://safebrowsing.googleapis.com/v4/threatLists/malware.json?key=YOUR_API_KEY');\n$phishList = file_get_contents('https://safebrowsing.googleapis.com/v4/threatLists/phish.json?key=YOUR_API_KEY');\n\n// Check if the hash is in the lists\nif (in_array($hash, json_decode($malwareList, true))) {\n    echo 'Malware detected!';\n} elseif (in_array($hash, json_decode($phishList, true))) {\n    echo 'Phishing detected!';\n} else {\n    echo 'No threats found.';\n}