// application/models/Product.php\nclass Product extends CI_Model {\n    public function get_products($limit = 20) {\n        // Database query to retrieve products\n        return $this->db->get('products', $limit)->result();\n    }\n}\n\n// application/libraries/ProductCollection.php\nclass ProductCollection {\n    private $products;\n\n    public function __construct($products) {\n        $this->products = $products;\n    }\n\n    public function get_products() {\n        return $this->products;\n    }\n}\n\n// application/controllers/ProductController.php\nclass ProductController extends CI_Controller {\n    public function index() {\n        $this->load->model('product');\n        $products = $this->product->get_products();\n        $product_collection = new ProductCollection($products);\n        // ...\n    }\n}