$pivotData = array();\n$rounds = array();\n\n// Assemble pivot data\nforeach ($scores as $score) {\n    if (!isset($pivotData[$score->Player_Name])) {\n        $pivotData[$score->Player_Name] = array();\n    }\n    $pivotData[$score->Player_Name][$score->Round_Name] = $score->Score;\n    $rounds[$score->Round_Name] = true; // Keep track of unique rounds\n}\n\n// Calculate totals and finalize pivot table structure\n$pivotTable = array();\n$roundNames = array_keys($rounds);\nsort($roundNames); // Ensure rounds are ordered\n\nforeach ($pivotData as $player => $scores) {\n    $row = array('Player' => $player);\n    $total = 0;\n    foreach ($roundNames as $roundName) {\n        $row[$roundName] = isset($scores[$roundName]) ? $scores[$roundName] : 0;\n        $total += $row[$roundName];\n    }\n    $row['Total'] = $total;\n    $pivotTable[] = $row;\n}\n\n// Print pivot table\necho "Player\t" . implode("\t", $roundNames) . "\tTotal\n";\nforeach ($pivotTable as $row) {\n    echo implode("\t", $row) . "\n";\n}