$contents = file_get_contents("summary.json");\n$c = json_decode($contents, true);\n\n// Get user input (e.g., keywords)\n$keywords = $_GET['keywords'];\n\n// Split keywords into an array\n$keywordArray = explode(' ', $keywords);\n\n// Filter the results\n$results = array();\nforeach ($c as $ind => $val) {\n    $match = true;\n    foreach ($keywordArray as $keyword) {\n        if (!in_array($keyword, $val['keywords'])) {\n            $match = false;\n            break;\n        }\n    }\n    if ($match) {\n        $results[] = $val;\n    }\n}\n\n// Display the results\nforeach ($results as $result) {\n    echo $result['title'] . "\n";\n    echo $result['description'] . "\n";\n}