$url = 'https://example.com/path/to/page';\n$html = file_get_contents($url);\n\n$dom = new DOMDocument();\n$dom->loadHTML($html);\n$images = $dom->getElementsByTagName('img');\n\nforeach ($images as $image) {\n    $src = $image->getAttribute('src');\n    $fullSrc = getFullSrc($url, $src);\n    echo $fullSrc . "\n";\n}\n\nfunction getFullSrc($baseUrl, $src) {\n    $baseUrlParts = parse_url($baseUrl);\n    if (strpos($src, '://') !== false) {\n        return $src; // already absolute\n    } elseif (strpos($src, '/') === 0) {\n        return $baseUrlParts['scheme'] . '://' . $baseUrlParts['host'] . $src;\n    } else {\n        return $baseUrlParts['scheme'] . '://' . $baseUrlParts['host'] . $baseUrlParts['path'] . '/' . $src;\n    }\n}