function xss_filter($input) {\n    $regex = array(\n        '/<script[^>]*>/i',\n        '/on\w+=/i',\n        '/javascript:/i',\n        '/data:text\/html;/i',\n        '/(alert|eval|unescape|exec|expression|setTimeout|setInterval)/i',\n        '/(iframe|object|embed|applet|param)/i',\n        '/(style|class|on\w+|src|href|data)/i'\n    );\n    foreach ($regex as $pattern) {\n        $input = preg_replace($pattern, '', $input);\n    }\n    return $input;\n}