// Connecting to a MySQL database\n$dsn = 'mysql:host=localhost;dbname=blog';\n$username = 'your_username';\n$password = 'your_password';\n$conn = new PDO($dsn, $username, $password);\n\n// Writing data to a MySQL database\n$stmt = $conn->prepare('INSERT INTO posts (title, content) VALUES (:title, :content)');\n$stmt->bindParam(':title', 'Example Post');\n$stmt->bindParam(':content', 'This is a sample post.');\n$stmt->execute();\n\n// Reading data from a MySQL database\n$stmt = $conn->prepare('SELECT * FROM posts WHERE id = 1');\n$stmt->execute();\n$row = $stmt->fetch(PDO::FETCH_ASSOC);\nprint_r($row);