// Assuming you have a PHP form that provides the necessary data\n\n$filmTitle = $_POST['filmTitle'];\n$runningTime = $_POST['runningTime'];\n$description = $_POST['description'];\n$year = $_POST['year'];\n$genre = $_POST['genre'];\n$director = $_POST['director'];\n$actors = $_POST['actors']; // Assuming this is an array of actor names\n\n// Insert film data\n$filmQuery = "INSERT INTO Films (filmtitle, runningtime, description) VALUES ('$filmTitle', '$runningTime', '$description')";\n$filmResult = mysqli_query($conn, $filmQuery);\n$filmId = mysqli_insert_id($conn);\n\n// Insert year data if it doesn't exist\n$yearQuery = "SELECT yearid FROM Years WHERE year = '$year'";\n$yearResult = mysqli_query($conn, $yearQuery);\nif (mysqli_num_rows($yearResult) == 0) {\n    $yearQuery = "INSERT INTO Years (year) VALUES ('$year')";\n    mysqli_query($conn, $yearQuery);\n    $yearId = mysqli_insert_id($conn);\n} else {\n    $yearRow = mysqli_fetch_assoc($yearResult);\n    $yearId = $yearRow['yearid'];\n}\n\n// Insert genre data if it doesn't exist\n$genreQuery = "SELECT genreid FROM Genres WHERE genre = '$genre'";\n$genreResult = mysqli_query($conn, $genreQuery);\nif (mysqli_num_rows($genreResult) == 0) {\n    $genreQuery = "INSERT INTO Genres (genre) VALUES ('$genre')";\n    mysqli_query($conn, $genreQuery);\n    $genreId = mysqli_insert_id($conn);\n} else {\n    $genreRow = mysqli_fetch_assoc($genreResult);\n    $genreId = $genreRow['genreid'];\n}\n\n// Insert director data if it doesn't exist\n$directorQuery = "SELECT directorid FROM Directors WHERE director = '$director'";\n$directorResult = mysqli_query($conn, $directorQuery);\nif (mysqli_num_rows($directorResult) == 0) {\n    $directorQuery = "INSERT INTO Directors (director) VALUES ('$director')";\n    mysqli_query($conn, $directorQuery);\n    $directorId = mysqli_insert_id($conn);\n} else {\n    $directorRow = mysqli_fetch_assoc($directorResult);\n    $directorId = $directorRow['directorid'];\n}\n\n// Insert actor data if it doesn't exist\nforeach ($actors as $actor) {\n    $actorQuery = "SELECT actorid FROM Actors WHERE actor_name = '$actor'";\n    $actorResult = mysqli_query($conn, $actorQuery);\n    if (mysqli_num_rows($actorResult) == 0) {\n        $actorQuery = "INSERT INTO Actors (actor_name) VALUES ('$actor')";\n        mysqli_query($conn, $actorQuery);\n        $actorId = mysqli_insert_id($conn);\n    } else {\n        $actorRow = mysqli_fetch_assoc($actorResult);\n        $actorId = $actorRow['actorid'];\n    }\n\n    // Insert film-actor relationship\n    $filmActorQuery = "INSERT INTO Film_Actors (filmid, actorid) VALUES ('$filmId', '$actorId')";\n    mysqli_query($conn, $filmActorQuery);\n}\n\n// Insert film-genre relationship\n$filmGenreQuery = "INSERT INTO Film_Genres (filmid, genreid) VALUES ('$filmId', '$genreId')";\nmysqli_query($conn, $filmGenreQuery);\n\n// Insert film-director relationship\n$filmDirectorQuery = "INSERT INTO Film_Directors (filmid, directorid) VALUES ('$filmId', '$directorId')";\nmysqli_query($conn, $filmDirectorQuery);\n\n// Insert film-year relationship\n$filmYearQuery = "INSERT INTO Film_Years (filmid, yearid) VALUES ('$filmId', '$yearId')";\nmysqli_query($conn, $filmYearQuery);