$odb_conn = new COM("ADODB.Connection");\n$connstr = "DRIVER={Microsoft Access Driver (*.mdb)}; DBQ=". $db_path.";";\n$odb_conn->open($connstr);\n\n// Retrieve metadata for tables\n$rs_meta = $odb_conn->OpenSchema(20, array(Null, Null, Null, "TABLE"));\n\n// You can also retrieve metadata for other schema types, such as:\n// 1: Tables\n// 2: Linked Tables\n// 3: Queries\n// 4: Forms\n// 5: Reports\n// 6: Macros\n// 7: Modules\n// 8: Databases\n// 9: Relationships\n// 10: Database Properties\n// 11: Query Plans\n// 12: Linked Databases\n// 20: Tables ( same as 1, but with more information )\n\n// Print the metadata\nwhile (!$rs_meta->EOF) {\n    print_r($rs_meta->Fields);\n    $rs_meta->MoveNext();\n}