function readLinesFromFile($filePath, $startLine, $endLine) {\n    $lines = [];\n    $lineCounter = 0;\n    $handle = fopen($filePath, 'r');\n    if ($handle) {\n        while (($line = fgets($handle)) !== false) {\n            $lineCounter++;\n            if ($lineCounter >= $startLine && $lineCounter <= $endLine) {\n                $lines[] = trim($line);\n            } elseif ($lineCounter > $endLine) {\n                break;\n            }\n        }\n        fclose($handle);\n    }\n    return $lines;\n}