$db = new PDO('sqlite:counter.db');\n$db->exec('CREATE TABLE IF NOT EXISTS counter (value INTEGER)');\n\n// Retrieve current counter value\n$stmt = $db->query('SELECT value FROM counter');\n$counter = $stmt->fetchColumn();\n\n// Increment counter\n$counter++;\n\n// Update counter value\n$stmt = $db->prepare('UPDATE counter SET value = :value');\n$stmt->bindParam(':value', $counter);\n$stmt->execute();