$file = 'settings.txt';\n$contents = file_get_contents($file);\n$lines = explode("\n", $contents);\n\n$variables = array();\nforeach ($lines as $line) {\n    if (preg_match('/^(\w+):\'(.*)\'$/', $line, $matches)) {\n        $variables[$matches[1]] = $matches[2];\n    }\n}\n\nextract($variables);\n\n// Now you can use the variables\necho $template;  // outputs: xaddict\necho $editor;    // outputs: true\necho $wysiwyg;   // outputs: false