// Procedural function\nfunction greet($name) {\n    echo "Hello, $name!";\n}\n\n// OOP equivalent\nclass Greeter {\n    private $name;\n\n    public function __construct($name) {\n        $this->name = $name;\n    }\n\n    public function greet() {\n        echo "Hello, " . $this->name . "!";\n    }\n}\n\n$greeter = new Greeter("John");\n$greeter->greet(); // Output: "Hello, John!"