// Container.php\nclass Container {\n    private $classes = [];\n\n    public function register($class, $instance) {\n        $this->classes[$class] = $instance;\n    }\n\n    public function get($class) {\n        if (isset($this->classes[$class])) {\n            return $this->classes[$class];\n        }\n        // Load the class and create an instance\n        $file = 'path/to/classes/' . $class . '.php';\n        require $file;\n        $instance = new $class();\n        $this->register($class, $instance);\n        return $instance;\n    }\n}\n\n// config.php\n$container = new Container();