class Test {\n    private $a;\n    private $b;\n\n    public function setB($val) {\n        $this->__set('b', $val);\n    }\n\n    function __set($key, $val) {\n        switch ($key) {\n            case 'b':\n                if (!is_numeric($val)) throw new Exception("Variable b must be numeric");\n                break;\n        }\n\n        echo ("__SET: Setting {$key} to {$val}<br/>");\n        $this->$key = $val;\n    }\n\n    function __get($key) {\n        return $this->$key;\n    }\n\n    function bMethod() {\n        $this->setB("World2");\n    }\n}