function generate_meta_tags($content) {\n  // META description\n  $description = preg_replace('/(.*?)\.(?:\s|$)/s', '$1', $content);\n  $description = strip_tags($description);\n  $description = substr($description, 0, 155); // max 155 characters\n\n  // META keywords\n  $stop_words = array('the', 'and', 'a', 'an', 'in', 'is', 'it', 'of', 'to', 'for', 'with');\n  $words = preg_split('/\b\W+\b/', strtolower($content));\n  $words = array_diff($words, $stop_words);\n  $words = array_count_values($words);\n  arsort($words);\n  $keywords = implode(', ', array_slice(array_keys($words), 0, 10));\n\n  return array(\n    'description' => $description,\n    'keywords' => $keywords\n  );\n}\n\n$content = // your CMS page content here\n$meta_tags = generate_meta_tags($content);\necho '<meta name="description" content="' . $meta_tags['description'] . '">';\necho '<meta name="keywords" content="' . $meta_tags['keywords'] . '">';