function generateSubsetWords($anagram) {\n    $subsetWords = [];\n    $letters = str_split($anagram);\n    sort($letters);\n\n    // Generate all possible subset words\n    for ($i = 1; $i < count($letters); $i++) {\n        $subset = [];\n        for ($j = 0; $j < $i; $j++) {\n            $subset[] = $letters[$j];\n        }\n        $subsetWord = implode('', $subset);\n        if (isAnagram($subsetWord, $anagram)) {\n            $subsetWords[] = $subsetWord;\n        }\n    }\n\n    return $subsetWords;\n}\n\nfunction isAnagram($word1, $word2) {\n    $letters1 = str_split($word1);\n    $letters2 = str_split($word2);\n    sort($letters1);\n    sort($letters2);\n    return $letters1 === $letters2;\n}\n\n$anagram = 'arrest';\n$subsetWords = generateSubsetWords($anagram);\nprint_r($subsetWords);