class QueryFailedException extends Exception\n{\n    const DATABASE_CONNECTION_ERROR = 100;\n    const QUERY_SYNTAX_ERROR = 101;\n    const DATA_INTEGRITY_ERROR = 102;\n\n    public function __construct($message, $code)\n    {\n        parent::__construct($message, $code);\n    }\n}\n\n// ...\n\nif (!$this->connection[0]->query($this->query)) {\n    throw new QueryFailedException($this->connection[0]->error, QueryFailedException::QUERY_SYNTAX_ERROR);\n}