function countWeekdays(DateTime $start, DateTime $end) {\n    $count = 0;\n    while ($start <= $end) {\n        if ($start->format('N') <= 5) { // Monday to Friday\n            $count++;\n        }\n        $start->add(new DateInterval('P1D'));\n    }\n    return $count;\n}\n\n$date1 = new DateTime('2022-01-01');\n$date2 = new DateTime('2022-01-15');\necho countWeekdays($date1, $date2); // outputs 10