$how_many = 1000000;\n$counter1 = 0;\n$counter2 = 0;\n\n$abc = array('boolean_lookup'=>TRUE, 'string_lookup'=>'something_else');\n\n$start = microtime(true);\nfor ($i = 0; $i < $how_many; $i++)\n{\n    if ($abc['boolean_lookup'])\n    {\n        $counter1++;\n    }\n}\n\necho (microtime(true) - $start);\n\necho '<hr>';\n\n$start = microtime(true);\nfor ($i = 0; $i < $how_many; $i++)\n{\n    if ($abc['string_lookup'] == 'something_else')\n    {\n        $counter2++;\n    }\n}\n\necho (microtime(true) - $start);