$dsn = 'mysql:host=your_host;dbname=your_database';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $dateString = '02/15/2023';\n    $dateObject = DateTime::createFromFormat('m/d/Y', $dateString);\n    $mysqlFormatDate = $dateObject->format('Y-m-d');\n\n    $stmt = $pdo->prepare("INSERT INTO your_table (date_column) VALUES (:date)");\n    $stmt->bindParam(':date', $mysqlFormatDate);\n    $stmt->execute();\n\n} catch(PDOException $e) {\n    echo 'ERROR: ' . $e->getMessage();\n}