function manual_date_diff($old_date, $new_date) {\n    list($old_year, $old_month, $old_day) = explode('-', $old_date);\n    list($new_year, $new_month, $new_day) = explode('-', $new_date);\n\n    $old_time = mktime(0, 0, 0, $old_month, $old_day, $old_year);\n    $new_time = mktime(0, 0, 0, $new_month, $new_day, $new_year);\n\n    // Calculate the difference in days\n    $diff = ($new_time - $old_time) / 86400; // 86400 seconds in a day\n\n    return round($diff); // Returns the exact number of days\n}\n\n// Example usage:\n$old_date = '2008-11-15';\n$new_date = '2009-03-18';\n\necho manual_date_diff($old_date, $new_date);