function is_mobile_safari() {\n    $user_agent = $_SERVER['HTTP_USER_AGENT'];\n    return strpos($user_agent, 'Mobile/') !== false && strpos($user_agent, 'Safari/') !== false;\n}\n\nif (is_mobile_safari()) {\n    // Serve specific CSS file and smaller thumbnails\n    echo '<link rel="stylesheet" type="text/css" href="mobile.css">';\n    // ...\n} else {\n    // Serve default CSS file and larger thumbnails\n    echo '<link rel="stylesheet" type="text/css" href="default.css">';\n    // ...\n}